package days03;

public class Ex07_03 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//비교 연산자: 크고 작고, 같고 다름을 비교
		
		int i = 10, j = 3;
		System.out.println(i > j); //true
		System.out.println(i < j); //false
		System.out.println(i >= j); //true
		System.out.println(i <= j); //false
		System.out.println(i == j); //false
		System.out.println(i != j); //true
		
		

	}

}
